'use strict';

async function beginRecordingMultipartUpload(client, {
  maxChunkSize,
  ...params
}) {
  await client.waitUntilAuthenticated();
  return await client.sendCommand({
    method: "Internal.beginRecordingMultipartUpload",
    params: {
      ...params,
      chunkSize: maxChunkSize
    }
  });
}

exports.beginRecordingMultipartUpload = beginRecordingMultipartUpload;
