'use strict';

var superstruct = require('superstruct');

const firstEnvValueOf = (...envKeys) => () => envKeys.reduce(
  (a, k) => a || (typeof k === "function" ? k(process.env) : process.env[k]),
  void 0
);
const envString = (...envKeys) => superstruct.defaulted(superstruct.string(), firstEnvValueOf(...envKeys));

exports.envString = envString;
exports.firstEnvValueOf = firstEnvValueOf;
