'use strict';

var getReplayPath = require('../getReplayPath.js');
var logger = require('../logger.js');
var config = require('./config.js');

function getRuntimePath() {
  const overridePathKey = `REPLAY_CHROMIUM_EXECUTABLE_PATH`;
  const overridePath = process.env[overridePathKey];
  if (overridePath) {
    logger.logDebug(`Using executable override for chromium: ${overridePath}`);
    return overridePath;
  }
  return getReplayPath.getReplayPath("runtimes", ...config.runtimeMetadata.path);
}

exports.getRuntimePath = getRuntimePath;
