'use strict';

var createDeferred = require('../async/createDeferred.js');
var waitForPackageInfo = require('./waitForPackageInfo.js');

async function initializePackageInfo({
  packageName,
  packageVersion
}) {
  if (waitForPackageInfo.deferredPackageInfo.status !== createDeferred.STATUS_RESOLVED) {
    waitForPackageInfo.deferredPackageInfo.resolve({ packageName, packageVersion });
  }
}

exports.initializePackageInfo = initializePackageInfo;
