'use strict';

function log(message) {
  message.split("\n").forEach((m) => console.log("[replay.io]:", m));
}
function warn(message, e) {
  console.warn("[replay.io]:", message);
  if (e && e instanceof Error) {
    console.warn("[replay.io]: Error:", e.message);
  }
}

exports.log = log;
exports.warn = warn;
